import React, { useContext } from 'react';
import NavBar from "../../components/NavBar";
import { MyContext } from '../../context';
import { Flex, Stack, Button } from '@chakra-ui/core';
import ProgressingBar from '../../components/Charts/ProgressingBar';
import ColumnChart from '../../components/Charts/ColumnChart';
import PieChart from "../../components/Charts/PieChart";

const Dashboard = ({history}) => {
    const context = useContext(MyContext)
    return (
        <MyContext.Consumer>
            {context => {
                const {isLogged, user} = context.state
                return(
                    <>
                        <NavBar />
                        <Flex align="center">
                            <Flex justify="center" h="90vh" w="20vw">
                                <Stack spacing={6}>
                                    <Button variantColor="teal" variant="ghost">
                                        Mi compañia
                                    </Button>
                                    <Button variantColor="teal" variant="ghost">
                                        Transferencias
                                    </Button>
                                    <Button variantColor="teal" variant="ghost">
                                        Centro de Atencion
                                    </Button>
                                    <Button variantColor="teal" variant="ghost">
                                        Mi suscripción
                                    </Button>
                                </Stack>
                            </Flex>
                            <Flex h="90vh" w="80vw" justify="space-between">
                                <ProgressingBar />
                                <ColumnChart />
                                <PieChart />
                                <ColumnChart />
                            </Flex>
                        </Flex>
                    </>
                )
            }}
        </MyContext.Consumer>
    );
};

export default Dashboard;
