import React, { useContext } from 'react';
import { 
    Flex, 
    Stack, 
    Text, 
    FormControl, 
    useToast, 
    InputLeftElement, 
    Button, 
    InputGroup, 
    Input, 
    InputRightElement,
    Box,
    Image,
    RadioGroup,
    Radio,
    Divider
 } from "@chakra-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faKey, faEyeSlash, faEye, faUser } from "@fortawesome/free-solid-svg-icons";
import Form from "../../components/Form";
import { MyContext } from '../../context';
import { Link } from "react-router-dom";

const Signup = ({history}) => {
    const context = useContext(MyContext)
    const toast = useToast()

    const [show, setShow] = React.useState(false);
    const handleClick = () => setShow(!show);

    const signup = e => {
        context
            .handleSignupSubmit(e)
            .then(res => {
                toast({
                title: "Felicidades!",
                description: "Tu cuenta se ha creado de manera correcta",
                status: "success",
                duration: 2500,
                isClosable: true
                });
                history.push('/dashboard')
            })
            .catch(err => {
                console.log(err);
                toast({
                title: "ERROR",
                description: `We cannot create your account ${err}`
                });
            });
    }

    return (
        <MyContext.Consumer>
            {context => {
                return(
                    <Flex
                        backgroundColor="green"
                        w="100vw"
                        h="100vh"
                        align="center"
                        justify="center"
                    >
                        <Flex align="center">
                            <Flex align="center" justify="center" size="600px">
                                <Stack spacing={0}>
                                    <Box>
                                        <Image src="/images/logo.png" alt="OysterLogo" />
                                    </Box>
                                    <Text fontSize="2xl" color="black">
                                        La cuenta inteligente para emprendedores como t√∫
                                    </Text>
                                </Stack>
                            </Flex>
                            <Flex size="700px" align="center" justify="center">
                                <Stack>
                                    <Form submit={signup} bgColor="transparent" titleButton="Abrir mi cuenta" title="Registrate">
                                        <FormControl isRequired>
                                            <InputGroup>
                                                <InputLeftElement
                                                    children={
                                                        <FontAwesomeIcon icon={faUser} size="1x" color="gray"/>
                                                    }
                                                />
                                                <Input
                                                    name="email"
                                                    value={context.state.formSignUp.name}
                                                    onChange={context.handleSignupInput}
                                                    type="email"
                                                    placeholder="Email"
                                                />
                                            </InputGroup>
                                        </FormControl>
                                        <FormControl isRequired>
                                            <InputGroup>
                                                <InputLeftElement
                                                    children={
                                                        <FontAwesomeIcon icon={faEnvelope} size="1x" color="gray"/>
                                                    }
                                                />
                                                <Input
                                                    name="email"
                                                    value={context.state.formSignUp.email}
                                                    onChange={context.handleSignupInput}
                                                    type="email"
                                                    placeholder="Email"
                                                />
                                            </InputGroup>
                                        </FormControl>
                                        <FormControl isRequired>
                                            <InputGroup>
                                                <InputLeftElement
                                                    children={
                                                        <FontAwesomeIcon icon={faKey} size="1x" color="gray" />
                                                    }
                                                />
                                                <Input
                                                    type={show ? "text" : "password"}
                                                    name="password"
                                                    value={context.state.formSignUp.password}
                                                    onChange={context.handleSignupInput}
                                                    placeholder="Password"
                                                />
                                                <InputRightElement width="4.5rem">
                                                    <Button h="1.75rem" size="sm" onClick={handleClick} backgroundColor="trasnparent">
                                                        {show ?  <FontAwesomeIcon icon={faEyeSlash} size="1x" color="gray" /> :  <FontAwesomeIcon icon={faEye} size="1x" color="gray" />}
                                                    </Button>
                                                </InputRightElement>
                                            </InputGroup>
                                        </FormControl>
                                        <Divider />
                                        <Text fontSize="md" color="black">
                                            Tipo de cuenta
                                        </Text>
                                        <RadioGroup 
                                            isInline 
                                            spacing={4}
                                            onChange={context.handleSignupRadio}
                                            value={context.state.formSignUp.accountType}
                                            >
                                            <Radio value="Fisica">
                                                Persona Fisica
                                            </Radio>
                                            <Radio value="Moral">
                                                Persona Moral
                                            </Radio>
                                        </RadioGroup>
                                        <Divider />
                                    </Form>
                                    <Text fontSize="sm" color="black">
                                        Aun no tienes cuenta? - <Link to="/signup">Registrate!!</Link>
                                    </Text>
                                </Stack>
                            </Flex>
                        </Flex>
                    </Flex>
                )
            }}
        </MyContext.Consumer>
    );
}

export default Signup;
