import React, { useContext } from 'react';
import { MyContext } from "../context";
import { Flex, useDisclosure, Image, Heading, Button, Box, Drawer, DrawerOverlay, DrawerContent, DrawerCloseButton, DrawerHeader, Stack, Avatar, DrawerBody, DrawerFooter, Text } from '@chakra-ui/core';
import { NavLink, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';

const NavBar = (props) => {
    const context = useContext(MyContext)

    const { isOpen, onOpen, onClose } = useDisclosure();
    const btnRef = React.useRef();  

    const logout = () => {
        context
            .handleLogout()
            .then()
            .catch()
    }

    return (
        <MyContext.Consumer>
            {context => {
                return (
                   <Flex
                        w="100"
                        h="10vh"
                        px={5}
                        alignItems="center"
                        justify="space-between"
                    >
                   <NavLink exact to="https://oyster.io/">
                     <Flex
                       flexDirection="row"
                       justifyContent="center"
                       alignItems="center"
                     >
                       <Image
                         size="50px"
                         objectFit="cover"
                         src="/images/logo2.png"
                         alt="OysterLogo"
                       />
                       <Heading px={3} as="h2" size="xl">
                         Oyster
                       </Heading>
                     </Flex>
                   </NavLink>
                   <Box>
                     <Button
                       ref={btnRef}
                       variantColor="transparent"
                       onClick={onOpen}
                     >
                       <FontAwesomeIcon icon={faBars} size="2x" color="#CA3E47" />
                     </Button>
                     <Drawer
                       isOpen={isOpen}
                       placement="right"
                       onClose={onClose}
                       finalFocusRef={btnRef}
                     >
                       <DrawerOverlay />
                       <DrawerContent>
                         <DrawerCloseButton />
                         <DrawerHeader>
                           <Stack isInline>
                             {/* <Avatar name={loggedUser.name} src={loggedUser.image} /> */}
                           </Stack>
                         </DrawerHeader>

                         <DrawerBody>
                           <Link to="/">Home</Link>
                           <br />
                           <br />
                           <Link to="/profile">Profile</Link>
                           <br />
                           <br />
                           <Link to="/profile/contacts">My Safe Contacts</Link>
                           <br />
                           <br />
                           <Link to="/profile/places">My Safe Places</Link>
                         </DrawerBody>

                         <DrawerFooter>
                           <Text fontSize="md">
                             Ironhack WDFT 2020 - oljudi
                           </Text>
                           <Button
                             variant="outline"
                             variantColor="red"
                             mr={3}
                             onClick={logout}
                           >
                             LogOut
                           </Button>
                         </DrawerFooter>
                       </DrawerContent>
                     </Drawer>
                   </Box>
                 </Flex>
                )
            }}
        </MyContext.Consumer>
    );
}

export default NavBar;
