import React, { useContext } from 'react';
import { 
    Flex, Stack, Text, FormControl, useToast, InputLeftElement, Button, InputGroup, Input
 } from "@chakra-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Form from "../../components/Form";
import { MyContext } from '../../context';

const Home = ({history}) => {
    const context = useContext(MyContext)
    const toast = useToast()

    const [show, setShow] = React.useState(false);
    const handleClick = () => setShow(!show);

    const login = e => {
        context
            .handleLoginSubmit(e)
            .then(res => {
                history.push("/dashboard")
            })
            .catch(err => {
                toast({
                    title: "Error",
                    description: "Usuario o contraseña incorrectos",
                    status: "error"
                })
            })
    }

    return (
        <MyContext.Consumer>
            {context => {
                return(
                    <Flex
                        backgroundColor="green"
                        w="100vw"
                        h="100vh"
                        align="center"
                        justify="center"
                    >
                        <Flex align="center">
                            <Flex bg="green.50" align="flex-end" size="700px">
                                <Text>Box 1</Text>
                            </Flex>
                            <Flex bg="blue.50" size="700px" align="center" justify="center">
                                <Stack>
                                    <Form submit={login} bgColor="transparent" title="Login">
                                        <FormControl isRequired>
                                            <InputGroup>
                                                <InputLeftElement
                                                    children={
                                                        <FontAwesomeIcon icon={faEnvelope} size="1x" color="gray"/>
                                                    }
                                                />
                                                <Input
                                                    name="email"
                                                    value={context.state.formLogin.email}
                                                    onChange={context.handleLoginInput}
                                                    type="email"
                                                    placeholder="Email"
                                                />
                                            </InputGroup>
                                        </FormControl>
                                        <FormControl isRequired>
                                            <InputGroup>
                                                <InputLeftElement
                                                    children={
                                                        <FontAwesomeIcon icon={faKey} size="1x" color="gray" />
                                                    }
                                                />
                                                <Input
                                                    type={show ? "text" : "password"}
                                                    name="password"
                                                    value={context.state.formLogin.password}
                                                    onChange={context.handleLoginInput}
                                                    placeholder="Password"
                                                />
                                                <InputRightElement width="4.5rem">
                                                    <Button h="1.75rem" size="sm" onClick={handleClick}>
                                                        {show ? "Hide" : "Show"}
                                                    </Button>
                                                </InputRightElement>
                                            </InputGroup>
                                        </FormControl>
                                    </Form>
                                    <Text fontSize="sm" color="white">
                                        Don't have an account? - <Link to="/signup">Signup</Link>
                                    </Text>
                                </Stack>
                            </Flex>
                        </Flex>
                    </Flex>
                )
            }}
        </MyContext.Consumer>
    );
}

export default Home;
